<?php/** * This is the model class for table "bo_infowizard_documentchklist". * * The followings are the available columns in table 'bo_infowizard_documentchklist': * @property integer $docchk_id * @property string $chklist_id * @property string $name * @property integer $doc_id * @property integer $issuer_id * @property integer $issuerby_id * @property integer $issmap_id * @property string $is_docchklist_active *  *  * @property string $date_of_issuance  * @property string $created_date */class InfowizardDocumentchklist extends CActiveRecord{	/**	 * @return string the associated database table name	 */	public function tableName()	{		return 'bo_infowizard_documentchklist';	}	/**	 * @return array validation rules for model attributes.	 */	public function rules()	{		// NOTE: you should only define rules for those attributes that		// will receive user inputs.		return array(			array('chklist_id, name, doc_id, issmap_id, created_date', 'required'),			array(', issmap_id', 'numerical', 'integerOnly'=>true),                    			array('doc_id', 'length', 'max'=>50),			array('chklist_id', 'length', 'max'=>50),                    array('chklist_id','unique', 'message'=>'Checklist Id already accupied.'),			array('name', 'length', 'max'=>200),			array('name','unique', 'message'=>'This document check list already exists.'),			array('is_docchklist_active', 'length', 'max'=>1),			/*			@Santosh Kumar			@01-05-2018			*/			array('is_multi_version_allowed', 'length', 'max'=>1),			array('is_validity_required', 'length', 'max'=>1),			array('is_document_reference_no_required', 'length', 'max'=>1),			/* --- END --- */						// The following rule is used by search().			// @todo Please remove those attributes that should not be searched.			array('docchk_id, chklist_id, name, doc_id, issmap_id, is_docchklist_active,date_of_issuance , created_date', 'safe', 'on'=>'search'),		);	}	/**	 * @return array relational rules.	 */	public function relations()	{		// NOTE: you may need to adjust the relation name and the related		// class name for the relations automatically generated below.		return array(		);	}	/**	 * @return array customized attribute labels (name=>label)	 */	public function attributeLabels()	{		return array(			'docchk_id' => 'Docchk',			'chklist_id' => 'Chklist',			'name' => 'Name',			'doc_id' => 'Doc',			'issmap_id' => 'Issmap',			'is_docchklist_active' => 'Is Docchklist Active',			/*			@Santosh Kumar			@01-05-2018			*/			'is_multi_version_allowed' => 'Is multi version allowed',			'is_validity_required' => 'Is validity required',			'is_document_reference_no_required' => 'Is document reference no required',			/* -- END -- */			'created_date' => 'Created Date',		);	}	/**	 * Retrieves a list of models based on the current search/filter conditions.	 *	 * Typical usecase:	 * - Initialize the model fields with values from filter form.	 * - Execute this method to get CActiveDataProvider instance which will filter	 * models according to data in model fields.	 * - Pass data provider to CGridView, CListView or any similar widget.	 *	 * @return CActiveDataProvider the data provider that can return the models	 * based on the search/filter conditions.	 */	public function search()	{		// @todo Please modify the following code to remove attributes that should not be searched.		$criteria=new CDbCriteria;		$criteria->compare('docchk_id',$this->docchk_id);		$criteria->compare('chklist_id',$this->chklist_id,true);		$criteria->compare('name',$this->name,true);		$criteria->compare('doc_id',$this->doc_id);		$criteria->compare('issmap_id',$this->issmap_id);		$criteria->compare('is_docchklist_active',$this->is_docchklist_active,true);		$criteria->compare('created_date',$this->created_date,true);		return new CActiveDataProvider($this, array(			'criteria'=>$criteria,		));	}	/**	 * Returns the static model of the specified AR class.	 * Please note that you should have this exact method in all your CActiveRecord descendants!	 * @param string $className active record class name.	 * @return InfowizardDocumentchklist the static model class	 */	public static function model($className=__CLASS__)	{		return parent::model($className);	}}