<?php	class InfowizardQuestionMasterExt extends SpApplications{	 public static function getMasterList($dbtable=null,$key=null,$value=null,$active=null,$isactivevalue=null){                                if(empty($active)){  $active="is_active"; }                     if(empty($isactivevalue)){ $isactivevalue="Y"; }		$connection=Yii::app()->db;			        $sql = "SELECT $key,$value FROM $dbtable";		$command=$connection->createCommand($sql);		$command->bindParam(":$active",$isactivevalue,PDO::PARAM_INT);		$allData=$command->queryAll();	                foreach ($allData as $data){                    $k=$data[$key];                    $listData[$k]=$data[$value];                                    }		return $listData;	}		          public static function getMasterName($dbtable=null,$key=null,$value=null,$id=null){                    		$connection=Yii::app()->db;		$isactive='Y'; 	        $sql = "SELECT $value FROM $dbtable where $id=$key";		$command=$connection->createCommand($sql);		$allData=$command->queryAll();	                		return $allData[0][$value];	}               public static function getMasterListIndustrial($dbtable=null,$key=null,$value=null,$active=null,$isactivevalue=null,$matchval=null){           		if(empty($active)){  $active="is_active"; }		if(empty($isactivevalue)){ $isactivevalue="Y"; }		$connection=Yii::app()->db;		$sql = "SELECT $key,$value FROM $dbtable where $active='Y' and land_type ='".$matchval."' Order By id ASC";		$command=$connection->createCommand($sql);		$command->bindParam(":$active",$isactivevalue,PDO::PARAM_INT);		$allData=$command->queryAll();			$listData = array();		foreach ($allData as $data){			$k=$data[$key];			$listData[$k]=$data[$value];		}		return $listData;	}}		?>